'use strict';

var fs = require('fs');
var express = require('express');
var mongo = require('mongodb').MongoClient;
var app = express();
var mongoUrl = "mongodb://" + process.env.DBUSER + ":" + process.env.DBPASS + "@ds119345.mlab.com:19345/freecodecamp_backend_projects";

if (!process.env.DISABLE_XORIGIN) {
  app.use(function(req, res, next) {
    var allowedOrigins = ['https://narrow-plane.gomix.me', 'https://www.freecodecamp.com'];
    var origin = req.headers.origin || '*';
    if(!process.env.XORIG_RESTRICT || allowedOrigins.indexOf(origin) > -1){
         console.log(origin);
         res.setHeader('Access-Control-Allow-Origin', origin);
         res.header("Access-Control-Allow-Headers", "Origin, X-Requested-With, Content-Type, Accept");
    }
    next();
  });
}

app.use('/public', express.static(process.cwd() + '/public'));

app.route('/_api/package.json')
  .get(function(req, res, next) {
    console.log('requested');
    fs.readFile(__dirname + '/package.json', function(err, data) {
      if(err) return next(err);
      res.type('txt').send(data.toString());
    });
  });
  
app.route('/')
    .get(function(req, res) {
		  res.sendFile(process.cwd() + '/views/index.html');
    });

app.get('/cat', function (request, response) {
  mongo.connect(mongoUrl, function(err, db) {
    if (err) throw err;
    var urlCatalogue = db.collection('urlCatalogue');
    urlCatalogue.find()
                .project({_id:0})
                .toArray(function(err, docs) {
                  if (err) throw err;
                  response.send(docs);
                  db.close();
                });
  });
});

app.get(/(new\/https?:\/\/([A-Za-z0-9]+\.)+[A-Za-z][A-Za-z]+\.?(\:[A-Za-z0-9]+)?\/?(\/[^ ]+)?)$/, function (request, response) {
  var shortUrl = getRandomInt(1000, 10000);
  mongo.connect(mongoUrl, function(err, db) {
    if (err) throw err;
    var urlCatalogue = db.collection('urlCatalogue'),
        url = {
          original_url: request.url.substring(5),
          short_url: "https://jcasabar-url-shortener.glitch.me/" + shortUrl
        };
    response.send(url);
    urlCatalogue.find({
      short_url: "https://jcasabar-url-shortener.glitch.me/" + shortUrl
    }, {
      _id: 0
    }).toArray(function(err, docs) {
      if (err) throw err;
      if (!docs[0]) {
        urlCatalogue.insert(url, function(err, data) {
          if (err) throw err;
          db.close();
        });
      } else {
        urlCatalogue.update({
          short_url: "https://jcasabar-url-shortener.glitch.me/" + shortUrl
        }, {
          $set: {
            original_url: request.url.substring(5)
          }
        }, function(err) {
          if (err) throw err;
          db.close();
        });
      };
      db.close();
    });
  });
});

app.get(/\d{4}/, function(request, response) {
  var shortUrl = request.url.substring(1);
  mongo.connect(mongoUrl, function(err, db) {
    if (err) throw err;
    var urlCatalogue = db.collection('urlCatalogue');
    urlCatalogue.find({
      short_url: "https://jcasabar-url-shortener.glitch.me/" + shortUrl
    }, {
      _id: 0
    }).toArray(function(err, docs) {
      if (err) throw err;
      if (!docs[0]) {
        response.type('txt').send('Shortened url is referring to an absent link, please try again.');
      } else {
        response.redirect(docs[0].original_url);
      };
      db.close();
    });
  });
});
  
